import { Component, Input, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cva, type VariantProps } from 'class-variance-authority';

const badgeVariants = cva(
  'inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
  {
    variants: {
      variant: {
        default: 'border-transparent bg-primary text-primary-foreground hover:bg-primary/80',
        secondary: 'border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80',
        destructive: 'border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80',
        outline: 'text-foreground border-border',
        success: 'border-transparent bg-green-500 text-white hover:bg-green-600',
        warning: 'border-transparent bg-yellow-500 text-white hover:bg-yellow-600',
        info: 'border-transparent bg-blue-500 text-white hover:bg-blue-600',
      },
      size: {
        default: 'px-2.5 py-0.5 text-xs',
        sm: 'px-2 py-0.5 text-xs',
        lg: 'px-3 py-1 text-sm',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
);

export interface BadgeProps extends VariantProps<typeof badgeVariants> {
  dismissible?: boolean;
}

/**
 * Badge component for status indicators and labels
 * Supports multiple variants, sizes, and optional icon integration
 */
@Component({
  selector: 'ng-shadcn-badge',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="computedClasses()" [attr.role]="role">
      <!-- Leading icon slot -->
      <span *ngIf="hasLeadingIcon" class="mr-1 flex-shrink-0">
        <ng-content select="[slot=leading-icon]"></ng-content>
      </span>

      <!-- Badge content -->
      <span class="truncate">
        <ng-content></ng-content>
      </span>

      <!-- Trailing icon slot -->
      <span *ngIf="hasTrailingIcon" class="ml-1 flex-shrink-0">
        <ng-content select="[slot=trailing-icon]"></ng-content>
      </span>

      <!-- Dismiss button -->
      <button
        *ngIf="dismissible"
        type="button"
        class="ml-1 flex-shrink-0 rounded-full p-0.5 hover:bg-black/10 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-1"
        (click)="onDismiss()"
        [attr.aria-label]="'Remove ' + (ariaLabel || 'badge')"
      >
        <svg
          class="h-3 w-3"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M6 18L18 6M6 6l12 12"
          />
        </svg>
      </button>
    </div>
  `,
})
export class BadgeComponent implements BadgeProps {
  @Input() variant: BadgeProps['variant'] = 'default';
  @Input() size: BadgeProps['size'] = 'default';
  @Input() dismissible = false;
  @Input() className = '';
  @Input() role = 'status';
  @Input() ariaLabel?: string;

  // Signals for reactive state
  private variantSignal = signal(this.variant);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  // Check for projected content
  hasLeadingIcon = false;
  hasTrailingIcon = false;

  // Computed properties
  computedClasses = computed(() => {
    return badgeVariants({
      variant: this.variantSignal(),
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  ngOnInit() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngAfterContentInit() {
    // In a real implementation, you would check for projected content
    // For now, we'll assume content exists
    this.hasLeadingIcon = true;
    this.hasTrailingIcon = true;
  }

  onDismiss() {
    // Emit dismiss event or handle removal
    const event = new CustomEvent('dismiss', {
      bubbles: true,
      cancelable: true,
    });

    // Dispatch the event on the host element
    const hostElement = document.querySelector('ng-shadcn-badge');
    hostElement?.dispatchEvent(event);
  }
}

import { Component, Input, Output, EventEmitter, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cva, type VariantProps } from 'class-variance-authority';

const buttonVariants = cva(
  'inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground hover:bg-primary/90',
        destructive: 'bg-destructive text-destructive-foreground hover:bg-destructive/90',
        outline: 'border border-input bg-background hover:bg-accent hover:text-accent-foreground',
        secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'text-primary underline-offset-4 hover:underline',
      },
      size: {
        default: 'h-10 px-4 py-2',
        sm: 'h-9 rounded-md px-3',
        lg: 'h-11 rounded-md px-8',
        icon: 'h-10 w-10',
      },
    },
    defaultVariants: {
      variant: 'default',  
      size: 'default',
    },
  }
);

export interface ButtonProps extends VariantProps<typeof buttonVariants> {
  disabled?: boolean;
}

/**
 * Button component with multiple variants and sizes
 * Supports both signal and observable patterns
 */
@Component({
  selector: 'ng-shadcn-button',
  standalone: true,
  imports: [CommonModule],
  template: `
    <button 
      [class]="computedClasses()"
      [disabled]="disabled"
      (click)="handleClick($event)"
      type="button"
    >
      <ng-content></ng-content>
    </button>
  `,
})
export class ButtonComponent implements ButtonProps {
  @Input() variant: ButtonProps['variant'] = 'default';
  @Input() size: ButtonProps['size'] = 'default';
  @Input() disabled = false;
  @Input() className = '';

  @Output() clicked = new EventEmitter<Event>();

  // Signal-based computed property for classes
  private variantSignal = signal(this.variant);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  computedClasses = computed(() => {
    return buttonVariants({
      variant: this.variantSignal(),
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  ngOnInit() {
    // Update signals when inputs change
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  handleClick(event: Event) {
    if (!this.disabled) {
      this.clicked.emit(event);
    }
  }
}

import { Component, Input, Output, EventEmitter, computed, signal, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { cva, type VariantProps } from 'class-variance-authority';

const checkboxVariants = cva(
  'peer h-4 w-4 shrink-0 rounded-sm border border-primary ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50',
  {
    variants: {
      size: {
        default: 'h-4 w-4',
        sm: 'h-3 w-3',
        lg: 'h-5 w-5',
      },
    },
    defaultVariants: {
      size: 'default',
    },
  }
);

export interface CheckboxProps extends VariantProps<typeof checkboxVariants> {
  disabled?: boolean;
  indeterminate?: boolean;
}

/**
 * Checkbox component with indeterminate state, accessibility features, and form integration
 * Supports signal-based state management and multiple sizes
 */
@Component({
  selector: 'ng-shadcn-checkbox',
  standalone: true,
  imports: [CommonModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CheckboxComponent),
      multi: true,
    },
  ],
  template: `
    <div class="flex items-center space-x-2">
      <button
        type="button"
        role="checkbox"
        [class]="computedClasses()"
        [class.bg-primary]="checked() && !indeterminate"
        [class.border-primary]="checked() && !indeterminate"
        [class.bg-primary]="indeterminate"
        [class.border-primary]="indeterminate"
        [disabled]="disabled"
        [attr.aria-checked]="indeterminate ? 'mixed' : checked()"
        [attr.aria-disabled]="disabled"
        [attr.aria-label]="ariaLabel"
        [attr.aria-describedby]="ariaDescribedby"
        (click)="toggle()"
        (keydown)="onKeydown($event)"
      >
        <!-- Checked state -->
        <svg
          *ngIf="checked() && !indeterminate"
          class="h-full w-full text-primary-foreground"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <path
            fill-rule="evenodd"
            d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
            clip-rule="evenodd"
          />
        </svg>

        <!-- Indeterminate state -->
        <svg
          *ngIf="indeterminate"
          class="h-full w-full text-primary-foreground"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <rect x="4" y="9" width="12" height="2" rx="1" />
        </svg>
      </button>

      <!-- Label content -->
      <div *ngIf="hasLabelContent" class="grid gap-1.5 leading-none">
        <label
          class="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
          [class.text-xs]="size === 'sm'"
          [class.text-base]="size === 'lg'"
          (click)="toggle()"
        >
          <ng-content select="[slot=label]"></ng-content>
        </label>
        <p
          *ngIf="hasDescriptionContent"
          class="text-xs text-muted-foreground"
          [class.text-xs]="size === 'sm'"
          [class.text-sm]="size === 'lg'"
        >
          <ng-content select="[slot=description]"></ng-content>
        </p>
      </div>
    </div>
  `,
})
export class CheckboxComponent implements CheckboxProps, ControlValueAccessor {
  @Input() disabled = false;
  @Input() indeterminate = false;
  @Input() size: CheckboxProps['size'] = 'default';
  @Input() className = '';
  @Input() ariaLabel?: string;
  @Input() ariaDescribedby?: string;

  @Output() checkedChange = new EventEmitter<boolean>();

  // Signals for reactive state
  protected checked = signal(false);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  // Check if there's content projected
  hasLabelContent = false;
  hasDescriptionContent = false;

  // Computed properties
  computedClasses = computed(() => {
    return checkboxVariants({
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  // ControlValueAccessor implementation
  private onChange = (value: boolean) => {};
  private onTouched = () => {};

  writeValue(value: boolean): void {
    this.checked.set(!!value);
  }

  registerOnChange(fn: (value: boolean) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  ngOnInit() {
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngAfterContentInit() {
    // Check for projected content
    this.hasLabelContent = true; // Assume content exists for now
    this.hasDescriptionContent = true; // Assume content exists for now
  }

  toggle() {
    if (this.disabled) return;

    const newValue = !this.checked();
    this.checked.set(newValue);
    this.onChange(newValue);
    this.checkedChange.emit(newValue);
    this.onTouched();
  }

  onKeydown(event: KeyboardEvent) {
    if (event.key === ' ' || event.key === 'Enter') {
      event.preventDefault();
      this.toggle();
    }
  }
}

import { Injectable, ComponentRef, ViewContainerRef, signal } from '@angular/core';
import { DialogComponent } from './dialog.component';

export interface DialogConfig {
  title?: string;
  description?: string;
  showCloseButton?: boolean;
  backdrop?: boolean;
  size?: 'sm' | 'md' | 'lg' | 'xl';
}

@Injectable({
  providedIn: 'root'
})
export class DialogService {
  private dialogs = signal<ComponentRef<DialogComponent>[]>([]);

  open(config: DialogConfig = {}, viewContainerRef?: ViewContainerRef): ComponentRef<DialogComponent> {
    const dialogRef = viewContainerRef?.createComponent(DialogComponent) || 
                      this.createDialog(config);

    if (dialogRef) {
      // Configure dialog
      dialogRef.instance.title = config.title || '';
      dialogRef.instance.description = config.description || '';
      dialogRef.instance.showCloseButton = config.showCloseButton ?? true;
      dialogRef.instance.backdrop = config.backdrop ?? true;
      dialogRef.instance.size = config.size || 'md';

      // Handle close event
      dialogRef.instance.closed.subscribe(() => {
        this.close(dialogRef);
      });

      // Show dialog
      dialogRef.instance.open();

      // Track dialog
      this.dialogs.update(dialogs => [...dialogs, dialogRef]);
    }

    return dialogRef;
  }

  close(dialogRef: ComponentRef<DialogComponent>) {
    dialogRef.instance.close();

    // Remove from tracking
    this.dialogs.update(dialogs => 
      dialogs.filter(dialog => dialog !== dialogRef)
    );

    // Destroy component after animation
    setTimeout(() => {
      dialogRef.destroy();
    }, 300);
  }

  closeAll() {
    this.dialogs().forEach(dialog => {
      this.close(dialog);
    });
  }

  private createDialog(config: DialogConfig): ComponentRef<DialogComponent> | null {
    // This would need application root ViewContainerRef
    // For now, return null - dialogs should be created via template
    return null;
  }
}

import { Component, Input, Output, EventEmitter, signal, computed, ElementRef, ViewChild, HostListener, ContentChildren, QueryList, AfterContentInit } from '@angular/core';
import { CommonModule } from '@angular/common';

/**
 * Dropdown menu item interface
 */
export interface DropdownMenuItem {
  id: string;
  label: string;
  icon?: string;
  disabled?: boolean;
  separator?: boolean;
  shortcut?: string;
  action?: () => void;
}

/**
 * Individual dropdown menu item component
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu-item',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      [class]="computedClasses"
      [attr.data-disabled]="disabled"
      role="menuitem"
      [attr.aria-disabled]="disabled"
      [attr.tabindex]="disabled ? -1 : 0"
      (click)="handleClick()"
      (keydown)="handleKeyDown($event)">

      @if (icon) {
        <span class="mr-2 h-4 w-4" [innerHTML]="icon"></span>
      }

      <span class="flex-1">{{ label }}</span>

      @if (shortcut) {
        <span class="ml-auto text-xs tracking-widest opacity-60">{{ shortcut }}</span>
      }
    </div>
  `,
})
export class DropdownMenuItemComponent {
  @Input() label = '';
  @Input() icon = '';
  @Input() disabled = false;
  @Input() shortcut = '';
  @Input() className = '';

  @Output() itemSelected = new EventEmitter<void>();

  get computedClasses(): string {
    const baseClasses = 'relative flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none transition-colors';
    const stateClasses = this.disabled 
      ? 'pointer-events-none opacity-50' 
      : 'focus:bg-accent focus:text-accent-foreground hover:bg-accent hover:text-accent-foreground';

    return `${baseClasses} ${stateClasses} ${this.className}`;
  }

  handleClick(): void {
    if (!this.disabled) {
      this.itemSelected.emit();
    }
  }

  handleKeyDown(event: KeyboardEvent): void {
    if ((event.key === 'Enter' || event.key === ' ') && !this.disabled) {
      event.preventDefault();
      this.handleClick();
    }
  }
}

/**
 * Dropdown menu separator component
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu-separator',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="-mx-1 my-1 h-px bg-muted" role="separator"></div>
  `,
})
export class DropdownMenuSeparatorComponent {}

/**
 * Dropdown menu label component
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu-label',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="px-2 py-1.5 text-sm font-semibold">
      <ng-content></ng-content>
    </div>
  `,
})
export class DropdownMenuLabelComponent {
  @Input() className = '';
}

/**
 * Dropdown menu content container
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu-content',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      [class]="computedClasses"
      [attr.data-state]="isOpen ? 'open' : 'closed'"
      role="menu"
      [attr.aria-orientation]="'vertical'"
      [style.display]="isOpen ? 'block' : 'none'"
      [style.position]="'absolute'"
      [style.z-index]="50"
      [style.min-width.px]="minWidth"
      [style.top.px]="position.top"
      [style.left.px]="position.left">
      <ng-content></ng-content>
    </div>
  `,
})
export class DropdownMenuContentComponent {
  @Input() className = '';
  @Input() minWidth = 220;
  @Input() isOpen = false;
  @Input() position = { top: 0, left: 0 };

  get computedClasses(): string {
    const baseClasses = 'z-50 min-w-[8rem] overflow-hidden rounded-md border bg-popover p-1 text-popover-foreground shadow-md';
    const animationClasses = 'data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[side=bottom]:slide-in-from-top-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2 data-[side=top]:slide-in-from-bottom-2';

    return `${baseClasses} ${animationClasses} ${this.className}`;
  }
}

/**
 * Dropdown menu trigger component
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu-trigger',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      #triggerElement
      [attr.aria-haspopup]="'menu'"
      [attr.aria-expanded]="isOpen"
      [attr.data-state]="isOpen ? 'open' : 'closed'"
      (click)="toggle()"
      (keydown)="handleKeyDown($event)">
      <ng-content></ng-content>
    </div>
  `,
})
export class DropdownMenuTriggerComponent {
  @ViewChild('triggerElement', { static: true }) triggerElement!: ElementRef;

  @Input() isOpen = false;
  @Output() openChange = new EventEmitter<boolean>();

  toggle(): void {
    this.isOpen = !this.isOpen;
    this.openChange.emit(this.isOpen);
  }

  handleKeyDown(event: KeyboardEvent): void {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      this.toggle();
    } else if (event.key === 'ArrowDown' && !this.isOpen) {
      event.preventDefault();
      this.toggle();
    }
  }

  getPosition(): { top: number; left: number } {
    const rect = this.triggerElement.nativeElement.getBoundingClientRect();
    return {
      top: rect.bottom + window.scrollY,
      left: rect.left + window.scrollX
    };
  }
}

/**
 * Main dropdown menu component
 */
@Component({
  selector: 'ng-shadcn-dropdown-menu',
  standalone: true,
  imports: [CommonModule, DropdownMenuTriggerComponent, DropdownMenuContentComponent],
  template: `
    <div class="relative inline-block text-left" [class]="className">
      <ng-content select="ng-shadcn-dropdown-menu-trigger"></ng-content>
      <ng-content select="ng-shadcn-dropdown-menu-content"></ng-content>
    </div>
  `,
})
export class DropdownMenuComponent implements AfterContentInit {
  @Input() className = '';
  @Input() open = false;

  @Output() openChange = new EventEmitter<boolean>();

  @ContentChildren(DropdownMenuTriggerComponent) triggers!: QueryList<DropdownMenuTriggerComponent>;
  @ContentChildren(DropdownMenuContentComponent) contents!: QueryList<DropdownMenuContentComponent>;
  @ContentChildren(DropdownMenuItemComponent) menuItems!: QueryList<DropdownMenuItemComponent>;

  private isOpen = signal(false);

  // Computed property for open state
  openState = computed(() => this.isOpen());

  ngAfterContentInit(): void {
    // Set initial state
    this.isOpen.set(this.open);

    // Subscribe to trigger events
    this.triggers.forEach(trigger => {
      trigger.openChange.subscribe(open => {
        this.setOpen(open);
      });
    });

    // Subscribe to menu item selections
    this.menuItems.forEach(item => {
      item.itemSelected.subscribe(() => {
        this.setOpen(false);
      });
    });

    this.updateComponentStates();
  }

  ngOnChanges(): void {
    this.isOpen.set(this.open);
    this.updateComponentStates();
  }

  @HostListener('document:click', ['$event'])
  onDocumentClick(event: MouseEvent): void {
    const target = event.target as HTMLElement;
    const dropdownElement = (event.currentTarget as HTMLElement)?.querySelector('ng-shadcn-dropdown-menu');

    if (this.isOpen() && dropdownElement && !dropdownElement.contains(target)) {
      this.setOpen(false);
    }
  }

  @HostListener('document:keydown', ['$event'])
  onDocumentKeyDown(event: KeyboardEvent): void {
    if (event.key === 'Escape' && this.isOpen()) {
      this.setOpen(false);
    }
  }

  private setOpen(open: boolean): void {
    this.isOpen.set(open);
    this.openChange.emit(open);
    this.updateComponentStates();
  }

  private updateComponentStates(): void {
    const isOpen = this.isOpen();

    // Update trigger states
    this.triggers?.forEach(trigger => {
      trigger.isOpen = isOpen;
    });

    // Update content states
    this.contents?.forEach(content => {
      content.isOpen = isOpen;

      // Update position when opening
      if (isOpen && this.triggers.length > 0) {
        const position = this.triggers.first.getPosition();
        content.position = position;
      }
    });
  }
}

import { Component, Input, Output, EventEmitter, computed, signal, forwardRef, ContentChildren, QueryList, AfterContentInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { cva, type VariantProps } from 'class-variance-authority';

const radioVariants = cva(
  'aspect-square h-4 w-4 rounded-full border border-primary text-primary ring-offset-background focus:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50',
  {
    variants: {
      size: {
        default: 'h-4 w-4',
        sm: 'h-3 w-3',
        lg: 'h-5 w-5',
      },
    },
    defaultVariants: {
      size: 'default',
    },
  }
);

export interface RadioOption {
  value: string;
  label: string;
  description?: string;
  disabled?: boolean;
}

export interface RadioGroupProps extends VariantProps<typeof radioVariants> {
  disabled?: boolean;
  orientation?: 'horizontal' | 'vertical';
}

/**
 * Radio Group component with form integration and keyboard navigation
 * Supports both template-driven and reactive forms
 */
@Component({
  selector: 'ng-shadcn-radio-group',
  standalone: true,
  imports: [CommonModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => RadioGroupComponent),
      multi: true,
    },
  ],
  template: `
    <div
      role="radiogroup"
      [class]="containerClasses()"
      [attr.aria-label]="ariaLabel"
      [attr.aria-describedby]="ariaDescribedby"
      [attr.aria-disabled]="disabled"
      (keydown)="onKeydown($event)"
    >
      <div
