export class ThemeBuilderComponent {
  isDark = signal(false);

  toggleTheme() {
    this.isDark.update(value => !value);
    if (this.isDark()) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }

