import { Component, Input, Output, EventEmitter, signal, computed, ContentChildren, QueryList, AfterContentInit } from '@angular/core';
import { CommonModule } from '@angular/common';

/**
 * Accordion item interface
 */
export interface AccordionItem {
  id: string;
  title: string;
  content: string;
  disabled?: boolean;
  expanded?: boolean;
}

/**
 * Individual accordion content component
 */
@Component({
  selector: 'ng-shadcn-accordion-content',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div
      [class]="computedClasses"
      [attr.data-state]="isExpanded ? 'open' : 'closed'"
      role="region"
      [attr.aria-labelledby]="triggerId"
      [attr.id]="contentId"
      [style.display]="isExpanded ? 'block' : 'none'">
      <div class="pb-4 pt-0">
        <ng-content></ng-content>
      </div>
    </div>
  `,
})
export class AccordionContentComponent {
  @Input() value = '';
  @Input() className = '';
  @Input() isExpanded = false;

  get triggerId(): string {
    return `accordion-trigger-${this.value}`;
  }

  get contentId(): string {
    return `accordion-content-${this.value}`;
  }

  get computedClasses(): string {
    const baseClasses = 'overflow-hidden text-sm transition-all';
    const stateClasses = this.isExpanded 
      ? 'data-[state=open]:animate-accordion-down' 
      : 'data-[state=closed]:animate-accordion-up';

    return `${baseClasses} ${stateClasses} ${this.className}`;
  }
}

/**
 * Individual accordion trigger component
 */
@Component({
  selector: 'ng-shadcn-accordion-trigger',
  standalone: true,
  imports: [CommonModule],
  template: `
    <button
      [class]="computedClasses"
      [attr.data-state]="isExpanded ? 'open' : 'closed'"
      [disabled]="disabled"
      [attr.aria-expanded]="isExpanded"
      [attr.aria-controls]="contentId"
      [attr.id]="triggerId"
      (click)="handleClick()"
      (keydown)="handleKeyDown($event)">

      <ng-content></ng-content>

      <svg 
        class="h-4 w-4 shrink-0 transition-transform duration-200"
        [class.rotate-180]="isExpanded"
        fill="none" 
        stroke="currentColor" 
        viewBox="0 0 24 24">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7"/>
      </svg>
    </button>
  `,
})
export class AccordionTriggerComponent {
  @Input() value = '';
  @Input() className = '';
  @Input() disabled = false;
  @Input() isExpanded = false;

  @Output() itemToggled = new EventEmitter<string>();

  get triggerId(): string {
    return `accordion-trigger-${this.value}`;
  }

  get contentId(): string {
    return `accordion-content-${this.value}`;
  }

  get computedClasses(): string {
    const baseClasses = 'flex flex-1 items-center justify-between py-4 font-medium transition-all hover:underline';
    const stateClasses = this.disabled 
      ? 'pointer-events-none opacity-50' 
      : '[&[data-state=open]>svg]:rotate-180';

    return `${baseClasses} ${stateClasses} ${this.className}`;
  }

  handleClick(): void {
    if (!this.disabled) {
      this.itemToggled.emit(this.value);
    }
  }

  handleKeyDown(event: KeyboardEvent): void {
    if ((event.key === 'Enter' || event.key === ' ') && !this.disabled) {
      event.preventDefault();
      this.handleClick();
    }
  }
}

/**
 * Individual accordion item component
 */
@Component({
  selector: 'ng-shadcn-accordion-item',
  standalone: true,
  imports: [CommonModule, AccordionTriggerComponent, AccordionContentComponent],
  template: `
    <div
      [class]="computedClasses"
      [attr.data-state]="isExpanded ? 'open' : 'closed'"
      [attr.data-disabled]="disabled">
      <ng-content select="ng-shadcn-accordion-trigger"></ng-content>
      <ng-content select="ng-shadcn-accordion-content"></ng-content>
    </div>
  `,
})
export class AccordionItemComponent {
  @Input() value = '';
  @Input() className = '';
  @Input() disabled = false;
  @Input() isExpanded = false;

  get computedClasses(): string {
    const baseClasses = 'border-b';
    return `${baseClasses} ${this.className}`;
  }
}

/**
 * Main accordion component
 */
@Component({
  selector: 'ng-shadcn-accordion',
  standalone: true,
  imports: [CommonModule, AccordionItemComponent, AccordionTriggerComponent, AccordionContentComponent],
  template: `
    <div [class]="computedClasses">
      <ng-content></ng-content>
    </div>
  `,
})
export class AccordionComponent implements AfterContentInit {
  @Input() type: 'single' | 'multiple' = 'single';
  @Input() collapsible = false;
  @Input() defaultValue: string | string[] = '';
  @Input() value: string | string[] = '';
  @Input() className = '';

  @Output() valueChange = new EventEmitter<string | string[]>();

  @ContentChildren(AccordionItemComponent) accordionItems!: QueryList<AccordionItemComponent>;
  @ContentChildren(AccordionTriggerComponent) accordionTriggers!: QueryList<AccordionTriggerComponent>;
  @ContentChildren(AccordionContentComponent) accordionContents!: QueryList<AccordionContentComponent>;

  private expandedItems = signal<string[]>([]);

  // Computed property for expanded items
  expandedState = computed(() => this.expandedItems());

  ngAfterContentInit(): void {
    // Set initial expanded items
    const initialValue = this.value || this.defaultValue;
    if (initialValue) {
      const initialItems = Array.isArray(initialValue) ? initialValue : [initialValue];
      this.expandedItems.set(initialItems);
    }

    // Subscribe to trigger events
    this.accordionTriggers.forEach(trigger => {
      trigger.itemToggled.subscribe(value => {
        this.toggleItem(value);
      });
    });

    this.updateItemStates();
  }

  ngOnChanges(): void {
    if (this.value) {
      const valueArray = Array.isArray(this.value) ? this.value : [this.value];
      this.expandedItems.set(valueArray);
      this.updateItemStates();
    }
  }

  private toggleItem(value: string): void {
    const currentExpanded = this.expandedItems();
    let newExpanded: string[];

    if (this.type === 'single') {
      // Single mode: only one item can be expanded at a time
      if (currentExpanded.includes(value)) {
        // If collapsible is true, allow closing the current item
        newExpanded = this.collapsible ? [] : currentExpanded;
      } else {
        newExpanded = [value];
      }
    } else {
      // Multiple mode: multiple items can be expanded
      if (currentExpanded.includes(value)) {
        newExpanded = currentExpanded.filter(item => item !== value);
      } else {
        newExpanded = [...currentExpanded, value];
      }
    }

    this.expandedItems.set(newExpanded);

    // Emit the appropriate value format
    const emitValue = this.type === 'single' 
      ? (newExpanded.length > 0 ? newExpanded[0] : '')
      : newExpanded;

    this.valueChange.emit(emitValue);
    this.updateItemStates();
  }

  private updateItemStates(): void {
    const expandedItems = this.expandedItems();

    // Update item states
    this.accordionItems?.forEach(item => {
      item.isExpanded = expandedItems.includes(item.value);
    });

    // Update trigger states
    this.accordionTriggers?.forEach(trigger => {
      trigger.isExpanded = expandedItems.includes(trigger.value);
    });

    // Update content states
    this.accordionContents?.forEach(content => {
      content.isExpanded = expandedItems.includes(content.value);
    });
  }

  get computedClasses(): string {
    const baseClasses = 'w-full';
    return `${baseClasses} ${this.className}`;
  }
}

import { Component, Input, Output, EventEmitter, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cva, type VariantProps } from 'class-variance-authority';

const alertVariants = cva(
  'relative w-full rounded-lg border p-4 [&>svg~*]:pl-7 [&>svg+div]:translate-y-[-3px] [&>svg]:absolute [&>svg]:left-4 [&>svg]:top-4 [&>svg]:text-foreground',
  {
    variants: {
      variant: {
        default: 'bg-background text-foreground border-border',
        destructive: 'border-destructive/50 text-destructive dark:border-destructive [&>svg]:text-destructive',
        warning: 'border-yellow-500/50 text-yellow-800 bg-yellow-50 dark:border-yellow-500 dark:text-yellow-300 dark:bg-yellow-950/30 [&>svg]:text-yellow-600 dark:[&>svg]:text-yellow-300',
        success: 'border-green-500/50 text-green-800 bg-green-50 dark:border-green-500 dark:text-green-300 dark:bg-green-950/30 [&>svg]:text-green-600 dark:[&>svg]:text-green-300',
        info: 'border-blue-500/50 text-blue-800 bg-blue-50 dark:border-blue-500 dark:text-blue-300 dark:bg-blue-950/30 [&>svg]:text-blue-600 dark:[&>svg]:text-blue-300',
      },
    },
    defaultVariants: {
      variant: 'default',
    },
  }
);

export interface AlertProps extends VariantProps<typeof alertVariants> {
  dismissible?: boolean;
  title?: string;
}

/**
 * Alert component for notification messages
 * Supports multiple variants, dismissible option, and icon integration
 */
@Component({
  selector: 'ng-shadcn-alert',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div 
      [class]="computedClasses()"
      role="alert"
      [attr.aria-live]="ariaLive"
      [attr.aria-atomic]="ariaAtomic"
    >
      <!-- Default icons based on variant -->
      <svg
        *ngIf="!hasCustomIcon && showDefaultIcon"
        class="h-4 w-4"
        fill="none"
        stroke="currentColor"
        viewBox="0 0 24 24"
      >
        <path
          *ngIf="variant === 'destructive'"
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"
        />
        <path
          *ngIf="variant === 'warning'"
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"
        />
        <path
          *ngIf="variant === 'success'"
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
        />
        <path
          *ngIf="variant === 'info'"
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
        <path
          *ngIf="variant === 'default'"
          stroke-linecap="round"
          stroke-linejoin="round"
          stroke-width="2"
          d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
        />
      </svg>

      <!-- Custom icon slot -->
      <span *ngIf="hasCustomIcon">
        <ng-content select="[slot=icon]"></ng-content>
      </span>

      <div class="flex-1">
        <!-- Title -->
        <h5 
          *ngIf="title || hasTitleContent"
          class="mb-1 font-medium leading-none tracking-tight"
        >
          {{ title }}
          <ng-content select="[slot=title]"></ng-content>
        </h5>

        <!-- Description/Content -->
        <div class="text-sm [&_p]:leading-relaxed">
          <ng-content></ng-content>
        </div>
      </div>

      <!-- Dismiss button -->
      <button
        *ngIf="dismissible"
        type="button"
        class="absolute right-2 top-2 rounded-md p-1 hover:bg-black/5 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 dark:hover:bg-white/5"
        (click)="onDismiss()"
        [attr.aria-label]="'Dismiss ' + (title || 'alert')"
      >
        <svg
          class="h-4 w-4"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M6 18L18 6M6 6l12 12"
          />
        </svg>
      </button>
    </div>
  `,
})
export class AlertComponent implements AlertProps {
  @Input() variant: AlertProps['variant'] = 'default';
  @Input() dismissible = false;
  @Input() title?: string;
  @Input() className = '';
  @Input() showDefaultIcon = true;
  @Input() ariaLive: 'polite' | 'assertive' | 'off' = 'polite';
  @Input() ariaAtomic = true;

  @Output() dismissed = new EventEmitter<void>();

  // Signals for reactive state
  private variantSignal = signal(this.variant);
  private classNameSignal = signal(this.className);

  // Check for projected content
  hasCustomIcon = false;
  hasTitleContent = false;

  // Computed properties
  computedClasses = computed(() => {
    return alertVariants({
      variant: this.variantSignal(),
      className: this.classNameSignal(),
    });
  });

  ngOnInit() {
    this.variantSignal.set(this.variant);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.variantSignal.set(this.variant);
    this.classNameSignal.set(this.className);
  }

  ngAfterContentInit() {
    // In a real implementation, you would check for projected content
    // For now, we'll assume content might exist
    this.hasCustomIcon = false; // Set based on actual content projection
    this.hasTitleContent = false; // Set based on actual content projection
  }

  onDismiss() {
    this.dismissed.emit();
  }
}

import { Component, Input, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cva, type VariantProps } from 'class-variance-authority';

const badgeVariants = cva(
  'inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2',
  {
    variants: {
      variant: {
        default: 'border-transparent bg-primary text-primary-foreground hover:bg-primary/80',
        secondary: 'border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80',
        destructive: 'border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80',
        outline: 'text-foreground border-border',
        success: 'border-transparent bg-green-500 text-white hover:bg-green-600',
        warning: 'border-transparent bg-yellow-500 text-white hover:bg-yellow-600',
        info: 'border-transparent bg-blue-500 text-white hover:bg-blue-600',
      },
      size: {
        default: 'px-2.5 py-0.5 text-xs',
        sm: 'px-2 py-0.5 text-xs',
        lg: 'px-3 py-1 text-sm',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  }
);

export interface BadgeProps extends VariantProps<typeof badgeVariants> {
  dismissible?: boolean;
}

/**
 * Badge component for status indicators and labels
 * Supports multiple variants, sizes, and optional icon integration
 */
@Component({
  selector: 'ng-shadcn-badge',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="computedClasses()" [attr.role]="role">
      <!-- Leading icon slot -->
      <span *ngIf="hasLeadingIcon" class="mr-1 flex-shrink-0">
        <ng-content select="[slot=leading-icon]"></ng-content>
      </span>

      <!-- Badge content -->
      <span class="truncate">
        <ng-content></ng-content>
      </span>

      <!-- Trailing icon slot -->
      <span *ngIf="hasTrailingIcon" class="ml-1 flex-shrink-0">
        <ng-content select="[slot=trailing-icon]"></ng-content>
      </span>

      <!-- Dismiss button -->
      <button
        *ngIf="dismissible"
        type="button"
        class="ml-1 flex-shrink-0 rounded-full p-0.5 hover:bg-black/10 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-1"
        (click)="onDismiss()"
        [attr.aria-label]="'Remove ' + (ariaLabel || 'badge')"
      >
        <svg
          class="h-3 w-3"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M6 18L18 6M6 6l12 12"
          />
        </svg>
      </button>
    </div>
  `,
})
export class BadgeComponent implements BadgeProps {
  @Input() variant: BadgeProps['variant'] = 'default';
  @Input() size: BadgeProps['size'] = 'default';
  @Input() dismissible = false;
  @Input() className = '';
  @Input() role = 'status';
  @Input() ariaLabel?: string;

  // Signals for reactive state
  private variantSignal = signal(this.variant);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  // Check for projected content
  hasLeadingIcon = false;
  hasTrailingIcon = false;

  // Computed properties
  computedClasses = computed(() => {
    return badgeVariants({
      variant: this.variantSignal(),
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  ngOnInit() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngAfterContentInit() {
    // In a real implementation, you would check for projected content
    // For now, we'll assume content exists
    this.hasLeadingIcon = true;
    this.hasTrailingIcon = true;
  }

  onDismiss() {
    // Emit dismiss event or handle removal
    const event = new CustomEvent('dismiss', {
      bubbles: true,
      cancelable: true,
    });

    // Dispatch the event on the host element
    const hostElement = document.querySelector('ng-shadcn-badge');
    hostElement?.dispatchEvent(event);
  }
}

import { Component, Input, Output, EventEmitter, computed, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { cva, type VariantProps } from 'class-variance-authority';

const buttonVariants = cva(
  'inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50',
  {
    variants: {
      variant: {
        default: 'bg-primary text-primary-foreground hover:bg-primary/90',
        destructive: 'bg-destructive text-destructive-foreground hover:bg-destructive/90',
        outline: 'border border-input bg-background hover:bg-accent hover:text-accent-foreground',
        secondary: 'bg-secondary text-secondary-foreground hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'text-primary underline-offset-4 hover:underline',
      },
      size: {
        default: 'h-10 px-4 py-2',
        sm: 'h-9 rounded-md px-3',
        lg: 'h-11 rounded-md px-8',
        icon: 'h-10 w-10',
      },
    },
    defaultVariants: {
      variant: 'default',  
      size: 'default',
    },
  }
);

export interface ButtonProps extends VariantProps<typeof buttonVariants> {
  disabled?: boolean;
}

/**
 * Button component with multiple variants and sizes
 * Supports both signal and observable patterns
 */
@Component({
  selector: 'ng-shadcn-button',
  standalone: true,
  imports: [CommonModule],
  template: `
    <button 
      [class]="computedClasses()"
      [disabled]="disabled"
      (click)="handleClick($event)"
      type="button"
    >
      <ng-content></ng-content>
    </button>
  `,
})
export class ButtonComponent implements ButtonProps {
  @Input() variant: ButtonProps['variant'] = 'default';
  @Input() size: ButtonProps['size'] = 'default';
  @Input() disabled = false;
  @Input() className = '';

  @Output() clicked = new EventEmitter<Event>();

  // Signal-based computed property for classes
  private variantSignal = signal(this.variant);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  computedClasses = computed(() => {
    return buttonVariants({
      variant: this.variantSignal(),
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  ngOnInit() {
    // Update signals when inputs change
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.variantSignal.set(this.variant);
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  handleClick(event: Event) {
    if (!this.disabled) {
      this.clicked.emit(event);
    }
  }
}

import { Component, Input, Output, EventEmitter, computed, signal, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { cva, type VariantProps } from 'class-variance-authority';

const checkboxVariants = cva(
  'peer h-4 w-4 shrink-0 rounded-sm border border-primary ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50',
  {
    variants: {
      size: {
        default: 'h-4 w-4',
        sm: 'h-3 w-3',
        lg: 'h-5 w-5',
      },
    },
    defaultVariants: {
      size: 'default',
    },
  }
);

export interface CheckboxProps extends VariantProps<typeof checkboxVariants> {
  disabled?: boolean;
  indeterminate?: boolean;
}

/**
 * Checkbox component with indeterminate state, accessibility features, and form integration
 * Supports signal-based state management and multiple sizes
 */
@Component({
  selector: 'ng-shadcn-checkbox',
  standalone: true,
  imports: [CommonModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => CheckboxComponent),
      multi: true,
    },
  ],
  template: `
    <div class="flex items-center space-x-2">
      <button
        type="button"
        role="checkbox"
        [class]="computedClasses()"
        [class.bg-primary]="checked() && !indeterminate"
        [class.border-primary]="checked() && !indeterminate"
        [class.bg-primary]="indeterminate"
        [class.border-primary]="indeterminate"
        [disabled]="disabled"
        [attr.aria-checked]="indeterminate ? 'mixed' : checked()"
        [attr.aria-disabled]="disabled"
        [attr.aria-label]="ariaLabel"
        [attr.aria-describedby]="ariaDescribedby"
        (click)="toggle()"
        (keydown)="onKeydown($event)"
      >
        <!-- Checked state -->
        <svg
          *ngIf="checked() && !indeterminate"
          class="h-full w-full text-primary-foreground"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <path
            fill-rule="evenodd"
            d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
            clip-rule="evenodd"
          />
        </svg>

        <!-- Indeterminate state -->
        <svg
          *ngIf="indeterminate"
          class="h-full w-full text-primary-foreground"
          fill="currentColor"
          viewBox="0 0 20 20"
        >
          <rect x="4" y="9" width="12" height="2" rx="1" />
        </svg>
      </button>

      <!-- Label content -->
      <div *ngIf="hasLabelContent" class="grid gap-1.5 leading-none">
        <label
          class="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
          [class.text-xs]="size === 'sm'"
          [class.text-base]="size === 'lg'"
          (click)="toggle()"
        >
          <ng-content select="[slot=label]"></ng-content>
        </label>
        <p
          *ngIf="hasDescriptionContent"
          class="text-xs text-muted-foreground"
          [class.text-xs]="size === 'sm'"
          [class.text-sm]="size === 'lg'"
        >
          <ng-content select="[slot=description]"></ng-content>
        </p>
      </div>
    </div>
  `,
})
export class CheckboxComponent implements CheckboxProps, ControlValueAccessor {
  @Input() disabled = false;
  @Input() indeterminate = false;
  @Input() size: CheckboxProps['size'] = 'default';
  @Input() className = '';
  @Input() ariaLabel?: string;
  @Input() ariaDescribedby?: string;

  @Output() checkedChange = new EventEmitter<boolean>();

  // Signals for reactive state
  protected checked = signal(false);
  private sizeSignal = signal(this.size);
  private classNameSignal = signal(this.className);

  // Check if there's content projected
  hasLabelContent = false;
  hasDescriptionContent = false;

  // Computed properties
  computedClasses = computed(() => {
    return checkboxVariants({
      size: this.sizeSignal(),
      className: this.classNameSignal(),
    });
  });

  // ControlValueAccessor implementation
  private onChange = (value: boolean) => {};
  private onTouched = () => {};

  writeValue(value: boolean): void {
    this.checked.set(!!value);
  }

  registerOnChange(fn: (value: boolean) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  ngOnInit() {
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngOnChanges() {
    this.sizeSignal.set(this.size);
    this.classNameSignal.set(this.className);
  }

  ngAfterContentInit() {
    // Check for projected content
    this.hasLabelContent = true; // Assume content exists for now
    this.hasDescriptionContent = true; // Assume content exists for now
  }

  toggle() {
    if (this.disabled) return;

    const newValue = !this.checked();
    this.checked.set(newValue);
    this.onChange(newValue);
    this.checkedChange.emit(newValue);
    this.onTouched();
  }

  onKeydown(event: KeyboardEvent) {
    if (event.key === ' ' || event.key === 'Enter') {
      event.preventDefault();
      this.toggle();
    }
  }
}

