      [disabled]="disabled"
      role="tab"
      [attr.aria-selected]="isActive"
      [attr.aria-controls]="contentId"
      [attr.id]="tabId"
      [attr.tabindex]="isActive ? 0 : -1"
      (click)="handleClick()"
      (keydown)="handleKeyDown($event)">
      <ng-content></ng-content>
    </button>
