          name: 'dark',
          value: '#0a0a0a',
        }
      }
    },
  },

  globalTypes: {
    theme: {
      description: 'Global theme for components',
      defaultValue: 'light',
      toolbar: {
        title: 'Theme',
        icon: 'paintbrush',
        items: ['light', 'dark'],
        dynamicTitle: true,
      },
    },
  },

  decorators: [
    (story, context) => {
      const theme = context.globals.theme || 'light';
      
      // Apply theme to document element
      if (typeof document !== 'undefined') {
        document.documentElement.className = theme;
      }
      
      return story();
    },
  ],

  initialGlobals: {
    backgrounds: {
      value: 'light'
    }
  }
}

