          firstName: this.firstNameControl.value,
          lastName: this.lastNameControl.value,
          email: this.emailControl.value,
        };
      },
    },
  }),
  parameters: {
    docs: {
      description: {
        story: 'Integration with Angular Reactive Forms using FormControl.',
      },
    },
  },
}

